import React from 'react';
import PDF0 from '../form/0.pdf'
import PDF1 from '../form/1.pdf'
import PDF2 from '../form/2.pdf'
import PDF3 from '../form/3.pdf'
import PDF4 from '../form/4.pdf'
import PDF5 from '../form/5.pdf'
import PDF6 from '../form/6.pdf'
import PDF7 from '../form/7.pdf'
import PDF8 from '../form/8.pdf'
import PDF9 from '../form/9.pdf'
import PDF10 from '../form/10.pdf'
import IMG0 from '../image/form/0.png'
import IMG1 from '../image/form/1.png'
import IMG2 from '../image/form/2.png'
import IMG3 from '../image/form/3.png'
import IMG4 from '../image/form/4.png'
import IMG5 from '../image/form/5.png'
import IMG6 from '../image/form/6.png'
import IMG7 from '../image/form/7.png'
import IMG8 from '../image/form/8.png'
import IMG9 from '../image/form/9.png'
import IMG10 from '../image/form/10.png'

const forms = [
  {
    'url' : PDF1,
    'title' : 'General Adult History',
    'sample' : IMG1
  },
  {
    'url' : PDF2,
    'title' : 'Medical/Family History Questionaire',
    'sample' : IMG2
  },
  {
    'url' : PDF7,
    'title' : 'Preventive Screen Questionaire',
    'sample' : IMG7
  },
  {
    'url' : PDF8,
    'title' : 'HIPAA Complaint Authorization for Release of Health Information',
    'sample' : IMG8
  },
  {
    'url' : PDF6,
    'title' : 'Mental Health Questionaire Age 3-5',
    'sample' : IMG6
  },
  {
    'url' : PDF5,
    'title' : 'Mental Health Questionaire Age 6-9',
    'sample' : IMG5
  },
  {
    'url' : PDF3,
    'title' : 'Mental Health Questionaire Age 10-12',
    'sample' : IMG3
  },
  {
    'url' : PDF4,
    'title' : 'Mental Health Questionaire Age 13-20',
    'sample' : IMG4
  },
  {
    'url' : PDF9,
    'title' : '9 Month Questionaire',
    'sample' : IMG9
  },
  {
    'url' : PDF10,
    'title' : '12 Month Quetionaire',
    'sample' : IMG10
  },      
  {
    'url' : PDF0,
    'title' : '14 Month Questionaire',
    'sample' : IMG0
  },
];

// let slideIndex = 1;
// // Next/previous controls
// function plusSlides(n) {
//   showSlides(slideIndex += n);
// }

// // Thumbnail image controls
// function currentSlide(n) {
//   showSlides(slideIndex = n);
// }

class Slideshow extends React.Component{
  state = {
    slideIndex: 1,
    doc: {},
    formList: []
  }
  componentDidMount (){
    this.splitForms();
    setTimeout(()=>{
      this.addActive()
    },1000)

  }
  initiateDoc (){
    this.setState(()=>({
      doc:{
        'slides': document.getElementsByClassName("slides"),
        'dots': document.getElementsByClassName("dot")
      }
    }))
  }
  addActive (){
    const doc = document.getElementById('carousel1');
    doc.classList.add('active')
  }
  splitForms (){
    const splitBy = 4;
    let newFormList = [];
    while (forms.length > 0)
      newFormList.push(forms.splice(0,splitBy));
    this.setState(()=>({formList:newFormList}))
  }
  // showSlides(n) {
  //   if (n > this.state.doc.slides.length) {this.setState({slideIndex:1})};
  //   if (n < 1) {this.setState({slideIndex:this.state.doc.slides.length})};
  //   for (let i = 0; i < this.state.doc.slides.length; i++) {
  //     let slide = this.state.doc.slides[i];
  //     slide.style.display='none';
  //   }
  //   for (let i = 0; i < this.state.doc.dots.length; i++) {
  //     let slide = this.state.doc.dots[i]
  //     slide.className = this.state.doc.dots[i].className.replace(" active", "");
  //   }
  //   const slide = this.state.doc.slides[this.state.slideIndex-1]
  //   slide.style.display = "flex";
  //   const dot = this.state.doc.dots[this.state.slideIndex-1];
  //   dot.className += " active";
  // }
  // plusSlides(e,n) {
  //   e.preventDefault();
  //   this.showSlides(this.state.slideIndex += n);
  // }
  render(){
    return(
      <React.Fragment>
        <div id="caraousel" className="container-fluid carousel slide" data-ride="carousel">
          <ol class="carousel-indicators">
            <li data-target="#carousel" data-slide-to="0" class="active"></li>
            <li data-target="#carousel" data-slide-to="1"></li>
            <li data-target="#carousel" data-slide-to="2"></li>
          </ol>
          <div className="carousel-inner w-100">
          {this.state.formList.map((formChunk,index)=>(          
            <div id={`carousel${index}`} className="row carousel-item">          
              {formChunk.map(form=>(
                <div key={form.url} className="col-md-3 p-2">
                  <div className="h-100">
                    <img className="w-100" src={form.sample} alt="Need Form"/>
                    <a href={form.url} className="w-100">
                      <p className="text-center p-0 mx-2 my-1" style={{color:'white'}}><strong>{`${form.title}`}</strong></p>
                    </a>
                  </div>
                </div>
              ))}
            </div>
          ))}
          </div>
          <a class="carousel-control-prev" href="#carousel" role="button" data-slide="prev">
            <span class="carousel-control-prev-icon" aria-hidden="true"></span>
            <span class="sr-only">Previous</span>
          </a>
          <a class="carousel-control-next" href="#carousel" role="button" data-slide="next">
            <span class="carousel-control-next-icon" aria-hidden="true"></span>
            <span class="sr-only">Next</span>
          </a>
        </div>
      </React.Fragment>
    )
  }
}

export default Slideshow;
