import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}
const directSectionList = {
  'home':{
    'up':'home',
    'down':'contact'
  },
  'contact':{
    'up':'home',
    'down':'form'
  },
  'form':{
    'up':'contact',
    'down':'form'
  }
}

let isScrolling = false;

// left: 37, up: 38, right: 39, down: 40,
// spacebar: 32, pageup: 33, pagedown: 34, end: 35, home: 36
var keys = {37: 1, 38: 1, 39: 1, 40: 1};

function preventDefault(e) {
  e = e || window.event;
  if (e.preventDefault)
      e.preventDefault();
  e.returnValue = false;  
}

function preventDefaultForScrollKeys(e) {
    if (keys[e.keyCode]) {
        preventDefault(e);
        return false;
    }
}

function disableScroll() {
  if (window.addEventListener) // older FF
      window.addEventListener('DOMMouseScroll', preventDefault, );
  document.addEventListener('wheel', preventDefault,); // Disable scrolling in Chrome
  window.onwheel = preventDefault; // modern standard
  window.onmousewheel = document.onmousewheel = preventDefault; // older browsers, IE
  window.ontouchmove  = preventDefault; // mobile
  document.onkeydown  = preventDefaultForScrollKeys;
}

function enableScroll() {
    if (window.removeEventListener)
        window.removeEventListener('DOMMouseScroll', preventDefault, false);
    document.removeEventListener('wheel', preventDefault, {passive: false}); // Enable scrolling in Chrome
    window.onmousewheel = document.onmousewheel = null; 
    window.onwheel = null; 
    window.ontouchmove = null;  
    document.onkeydown = null;  
}

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
  }
  componentDidMount () {
    window.addEventListener('scroll',()=>this.scrollToNextSection())
  }
  componentDidUpdate () {
  }
  scrollToNextSection(){
    window.scrollBy(0,10);
    if(!isScrolling){
      isScrolling = true;
      const nextSection = this.determineNextSection();
      const nextSectionDoc = this.getDocument(nextSection)
      // nextSectionDoc.scrollIntoView({ block: 'start'})
      let stop = false;
      let startScroll = false;
      console.log('going')
      // while(!stop){
      //   console.log('running')
      //   if(!startScroll){
      //     console.log('start window')
      //     startScroll = true;
      //     window.scrollBy(0,1);
      //   }
      //   if (window.pageYOffset===nextSectionDoc.offsetTop){
      //     stop = true;
      //     window.scrollBy(0,0);
      //     this.setState(()=>({section:nextSection}));
      //   }
      // }
    }
  }
  getDocument (section) {
    return document.getElementById(section)
  }
  determineNextSection = () => {
    return directSectionList[this.state.section][this.relateViewToSection()]
  }
  handleNav = (e,anchor) => {
    this.setState((prevState)=>({section:anchor}));
  }
  relateViewToSection = () =>{
    const topBound = this.getDocument(this.state.section).getBoundingClientRect().top;
    if (topBound>0)
      return 'up';
    else if (topBound<0)
      return 'down';
    else
      return 'equal';
  }
  render() {
    return (
      <div className="h-100" style={{overflow:'initial'}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            key={key}
            theKey={key}
            backgroundStyle={sectionList[key].backgroundStyle} 
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
