import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'black'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'black'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}

class App extends Component {
  state = {
    section: 'home'
  }
  componentDidMount () {
    window.addEventListener('scroll', this.handleScroll)
    window.scroll(0,5);
  }
  componentDidUpdate () {
    window.scroll(0,5);
  }
  handleScroll = () => {
    this.setState((prevState)=>({section:'contact'}))
    let startPosition = 5;
  }
  handleNav = (e,anchor) => {
    e.preventDefault();
    this.setState((prevState)=>({section:anchor}));
  }
  render() {
    return (
      <div className="" style={{height:"100vh"}}>

        <Nav navStyle={sectionList[this.state.section]} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            backgroundStyle={sectionList[key].backgroundStyle} 
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
