import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      top: '0'
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '0'
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '100vh'
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      top: '200vh',
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '200vh'
    },
    body: Form
  }
}
const directSectionList = {
  'home':{
    'up':'home',
    'down':'contact'
  },
  'contact':{
    'up':'home',
    'down':'form'
  },
  'form':{
    'up':'contact',
    'down':'form'
  }
}

// left: 37, up: 38, right: 39, down: 40,
// spacebar: 32, pageup: 33, pagedown: 34, end: 35, home: 36
var keys = {37: 1, 38: 1, 39: 1, 40: 1};

function preventDefault(e) {
  e = e || window.event;
  if (e.preventDefault)
      e.preventDefault();
  e.returnValue = false;  
}

function preventDefaultForScrollKeys(e) {
    if (keys[e.keyCode]) {
        preventDefault(e);
        return false;
    }
}

function disableScroll() {
  if (window.addEventListener) // older FF
      window.addEventListener('DOMMouseScroll', preventDefault, false);
  document.addEventListener('wheel', preventDefault, {passive: false}); // Disable scrolling in Chrome
  window.onwheel = preventDefault; // modern standard
  window.onmousewheel = document.onmousewheel = preventDefault; // older browsers, IE
  window.ontouchmove  = preventDefault; // mobile
  document.onkeydown  = preventDefaultForScrollKeys;
}

function enableScroll() {
  if (window.removeEventListener)
      window.removeEventListener('DOMMouseScroll', preventDefault, false);
  document.removeEventListener('wheel', preventDefault, {passive: false}); // Enable scrolling in Chrome
  window.onmousewheel = document.onmousewheel = null; 
  window.onwheel = null; 
  window.ontouchmove = null;  
  document.onkeydown = null;  
}

let isScrolling = false;
let prevScroll = 0;
let startScroll = undefined;

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
    direction: 'down',
    sectionHeight: 0,
    sectionPosition: {
      contact:0,
      form:0
    },
    doc: {
      contact: undefined,
      form: undefined
    },
    sectionHash: {
      contact:2,
      form:3
    }
  }

  componentDidMount () {
    window.scrollTo(0,1)
    this.initializePositioningHeight();
    setTimeout(()=>{
      window.addEventListener('scroll',e=>this.move(e));
    },500)
  }
  componentDidUpdate(){
  }
  componentWillUnmount(){
    clearInterval(startScroll)
    window.removeEventListener('scroll',e=>this.move(e));
  }
  initializePositioningHeight () {
    const height = document.getElementById('home').offsetHeight;
    this.setState({sectionPosition:this.getPosition(),
      sectionHeight:height,
      doc:{
        contact: this.getDocument('contact'),
        form: this.getDocument('form')
      }
    })
  }
  getPosition(){
    return {
      contact: document.getElementById('contact').getBoundingClientRect().top,
      form: document.getElementById('form').getBoundingClientRect().top
    }
  }
  move(e){
    e.preventDefault();
    e.stopPropagation();
    e.stopImmediatePropagation();
    if(!isScrolling){
      isScrolling = true;
      const direction = this.relateViewToSection();
      window.scrollTo(0,1)
      disableScroll();
      console.log(direction)
      if(this.isAbleMoveSection('contact',direction))
        this.moveSection('contact',direction);
      // if(this.isAbleMoveSection('form',direction))
      //   this.moveSection('form',direction);
      this.setState((prevState)=>({
        sectionPosition:{
          contact:prevState.sectionPosition['contact'] - prevState.sectionHeight,
          form: prevState.sectionPosition['form'] - prevState.sectionHeight
        }
      }))
    }
  }
  isAbleMoveSection(section,direction){
    console.log(`${direction} + ${section}`)
    if(direction==='down'){
      if(this.state.sectionPosition[section]<=0)
        return false;
      return true;
    }
    else if (direction==='up'){
      if(this.state.sectionPosition[section]>=this.state.sectionHeight*this.state.sectionHash[section])
        return false;
      return true;
    }
    else
      return false;
  }
  moveSection(section,direction){
    let doc = this.state.doc[section]
    let docPosition = this.state.sectionPosition[section];
    let originalPosition = this.state.sectionPosition[section];
    let scrollLength = this.state.sectionHeight/1000;
    // console.log(docPosition)
    // console.log(`${this.state.sectionPosition[section]}`)
    // console.log(`${this.state.sectionHeight}`)
    // console.log(`${this.state.sectionPosition[section] - this.state.sectionHeight}`)

    console.log((originalPosition-this.state.sectionHeight).toString()[0])
    startScroll = setInterval(()=>{
      // console.log(docPosition)
      // console.log(parseInt(`${this.state.sectionPosition[section] - this.state.sectionHeight}`))
      // if(direction==='up')
      //   docPosition += 1;
      // else
        docPosition -= scrollLength;
        doc.style.top = docPosition + 'px';
      if(docPosition>-1){
        console.log(Math.abs(docPosition).toString());
        console.log(Math.abs(docPosition).toString()[0])
      }
      if((originalPosition-this.state.sectionHeight)>=Math.floor(docPosition)-1){
        clearInterval(startScroll)
        console.log('stop')
        isScrolling = false;
        doc.style.top = originalPosition-this.state.sectionHeight+1
        console.log(doc.style.top)
        enableScroll();
      }
    },1)
  }
  determineNextSection = () => {
    return directSectionList[this.state.section][this.relateViewToSection()]
  }
  handleNav = (e,anchor) => {
    this.setState((prevState)=>({section:anchor}));
  }
  relateViewToSection = () =>{
    const topBound = this.getDocument(this.state.section).getBoundingClientRect().top;
    if (topBound>prevScroll){
      prevScroll=topBound;
      return 'up';
    }
    else if (topBound<prevScroll){
      prevScroll=topBound;
        return 'down';
    }
    else
      return 'equal';
  }
  resetScroll = () => {
    window.scrollTo(0,1);
  }
  render() {
    return (
      <div className="h-100 w-100" style={{overflow:'initial'}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            key={key}
            theKey={key}
            backgroundStyle={sectionList[key].backgroundStyle} 
            sectionStyle={sectionList[key].sectionStyle}
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
