import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}
const directSectionList = {
  'home':{
    'down':'contact'
  },
  'contact':{
    'up':'home',
    'down':'form'
  },
  'form':{
    'up':'contact'
  }
}

let scrolling = false;

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
  }
  componentDidMount () {
    window.addEventListener('scroll', (e)=>this.handleScroll(e))
    window.location.replace('/#home')
  }
  handleScroll = (e) => {
    e.stopPropagation();
    this.resetScrolling()
    if (!scrolling){
      // let st = window.pageYOffset|| document.documentElement.scrollTop;
      // console.log(`offset: ${window.pageYOffset}, scrollTop: ${document.documentElement.scrollTop}, innerHeight: ${window.innerHeight}, calc: ${window.innerHeight+this.state.scrollLocation}`)
      // console.log(`new: ${st} > old: ${this.state.scrollLocation}`)
      // if ( this.state.scrollLocation >= 0){
      //   console.log('test')
      //   this.determineSection(true,this.state.section)
      // } else {
      //   this.determineSection(false,this.state.section)
      // }
      console.log(this.determineNextSection())
    } 
    scrolling = true;
  }
  resetScrolling = () => {
    setTimeout(()=>{
      scrolling=false;
    },200)
  }
  determineNextSection = () => {
    console.log(directSectionList[this.state.section][this.relateViewToSection()])
    return directSectionList[this.state.section][this.relateViewToSection()]
  }
  handleNav = (e,anchor) => {

    this.setState((prevState)=>({section:anchor}));
  }
  relateViewToSection = () =>{
    const topBound = document.getElementById(this.state.section).getBoundingClientRect().top;
    console.log(topBound)
    if (topBound>0)
      return 'up';
    else if (topBound<0)
      return 'down';
    else
      return 'equal';
  }
  render() {
    return (
      <div className="" style={{height:"100vh"}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            key={key}
            theKey={key}
            backgroundStyle={sectionList[key].backgroundStyle} 
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
