import React from 'react';
import PDF0 from '../form/0.pdf'
import PDF1 from '../form/1.pdf'
import PDF2 from '../form/2.pdf'
import PDF3 from '../form/3.pdf'
import PDF4 from '../form/4.pdf'
import PDF5 from '../form/5.pdf'
import PDF6 from '../form/6.pdf'
import PDF7 from '../form/7.pdf'
import PDF8 from '../form/8.pdf'
import PDF9 from '../form/9.pdf'
import PDF10 from '../form/10.pdf'
import IMG0 from '../image/form/0.png'
import IMG1 from '../image/form/1.png'
import IMG2 from '../image/form/2.png'
import IMG3 from '../image/form/3.png'
import IMG4 from '../image/form/4.png'
import IMG5 from '../image/form/5.png'
import IMG6 from '../image/form/6.png'
import IMG7 from '../image/form/7.png'
import IMG8 from '../image/form/8.png'
import IMG9 from '../image/form/9.png'
import IMG10 from '../image/form/10.png'

const forms = [
  {
    'url' : PDF1,
    'title' : 'General Adult History',
    'sample' : IMG1
  },
  {
    'url' : PDF2,
    'title' : 'Medical/Family History Questionaire',
    'sample' : IMG2
  },
  {
    'url' : PDF7,
    'title' : 'Preventive Screen Questionaire',
    'sample' : IMG7
  },
  {
    'url' : PDF8,
    'title' : 'HIPAA Complaint Authorization for Release of Health Information',
    'sample' : IMG8
  },
  {
    'url' : PDF6,
    'title' : 'Mental Health Questionaire Age 3-5',
    'sample' : IMG6
  },
  {
    'url' : PDF5,
    'title' : 'Mental Health Questionaire Age 6-9',
    'sample' : IMG5
  },
  {
    'url' : PDF3,
    'title' : 'Mental Health Questionaire Age 10-12',
    'sample' : IMG3
  },
  {
    'url' : PDF4,
    'title' : 'Mental Health Questionaire Age 13-20',
    'sample' : IMG4
  },
  {
    'url' : PDF9,
    'title' : '9 Month Questionaire',
    'sample' : IMG9
  },
  {
    'url' : PDF10,
    'title' : '12 Month Quetionaire',
    'sample' : IMG10
  },      
  {
    'url' : PDF0,
    'title' : '14 Month Questionaire',
    'sample' : IMG0
  },
];

// let slideIndex = 1;
// // Next/previous controls
// function plusSlides(n) {
//   showSlides(slideIndex += n);
// }

// // Thumbnail image controls
// function currentSlide(n) {
//   showSlides(slideIndex = n);
// }

const buttonStyle ={
  backgroundColor:'rgba(0,0,0,0)',
  border:'0', 
  outline:'none',
}


class Slideshow extends React.Component{
  state = {
    slideIndex: 0,
    doc: {},
    formList: []
  }
  componentDidMount (){
    this.splitForms();
    setTimeout(()=>{
      this.addActive()
    },1000)

  }
  componentDidUpdate () {
    this.noDisplaySlide();
    this.displayCurrentSlide();
  }
  addActive (){
    const carousel = document.getElementById('carousel0');
    carousel.classList.add('active');
    const indicator = document.getElementById('indicator0');
    indicator.classList.add('active');
  }
  splitForms (){
    const splitBy = 4;
    let newFormList = [];
    while (forms.length > 0)
      newFormList.push(forms.splice(0,splitBy));
    this.setState(()=>({formList:newFormList}))
  }
  prevSlide (e) {
    e.preventDefault();
    if(this.state.slideIndex===0)
      this.setState({slideIndex:this.state.formList.length-1});
    else
      this.setState((prevState)=>({slideIndex:prevState.slideIndex-1}));
  }
  nextSlide () {
  }
  noDisplaySlide () {
    const carouselList = document.getElementsByClassName('item');
    for(let carousel of carouselList){
      carousel.style.display = "none";
      carousel.classList.remove('active');
    }
  }
  displayCurrentSlide() {
    const item = document.getElementById(`carousel${this.state.slideIndex}`);
    item.style.display = "flex";
    item.classList.add('active');
  }
  render(){
    return(
      <React.Fragment>
        <div id="slideshow" className="h-50 m-2 align-self-center" style={{backgroundColor:'rgba(255, 255, 255, 0.2)'}}>
          <ol className="carousel-indicators">
            {this.state.formList.map((form,index)=>(
              <li key={index} id={`indicator${index}`} data-target="#carousel"></li>
            ))}
          </ol>
          <div className="w-100 d-sm-none d-md-flex h-100">
            <button className="col-md-1 p-0" onClick={(e)=>this.prevSlide(e)} style={{backgroundImage:'linear-gradient(to right, rgba(255,255,255,0.8),rgba(255,255,255,0))', ...buttonStyle}}>&#10094;</button>
            <div className="col-md-10">
            {this.state.formList.map((formChunk,index)=>(          
              <div key={index} id={`carousel${index}`} className="item col-md-12">   
              {formChunk.map(form=>(
                <div key={form.url} className="col-md-3 p-2">
                  <a href={form.url} className="h-100 w-100">
                    <img className="w-100" src={form.sample} alt="Need Form"/>
                    <p className="text-center p-0 mx-2 my-1" style={{color:'white'}}><strong>{`${form.title}`}</strong></p>
                  </a>
                </div>
              ))}
              </div>
            ))}
            </div>
            <button className="col-md-1 p-0" onClick={(e)=>this.prevSlide(e)} style={{backgroundColor:'rgba(0,0,0,0)', border:'0', outline:'none'}}>&#10095;</button>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default Slideshow;
