import React, { Component } from 'react';
import './App.css';

import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'black'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100%',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      overflowY: 'scroll'
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100%',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100%',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}

class App extends Component {
  state = {
    section: 'home'
  }
  componentDidMount () {
    window.addEventListener('scroll', function() {console.log('test')})
  }
  handleScroll (event) {
    console.log('test')
    this.setState((prevState)=>({section:'contact'}))
  }
  render() {
    return (
      <div className="h-100">
        <Section navStyle={sectionList[this.state.section].navStyle} backgroundStyle={sectionList[this.state.section].backgroundStyle} body={sectionList[this.state.section].body}/>
      </div>
    );
  }
}

export default App;
