import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}

let scrolling = false;

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
  }
  componentDidMount () {
    window.location.replace('/#home')
    window.addEventListener('scroll', (e)=>this.handleScroll(e))
    window.scrollBy(0,2)
  }
  handleScroll = (e) => {
    e.stopPropagation();
    this.resetScrolling()
    if (!scrolling){
      let st = window.pageYOffset || document.documentElement.scrollTop;

      let el = document.getElementById('form')
      console.log(el.scrollTop)

      console.log(`offset: ${window.pageYOffset}, scrollTop: ${document.documentElement.scrollTop}`)
      console.log(`new: ${st} > old: ${this.state.scrollLocation}`)
      if ( st > this.state.scrollLocation){
        this.determineSection(true,this.state.section)
      } else {
        this.determineSection(false,this.state.section)
      }
    } 
    scrolling = true;
  }
  resetScrolling = () => {
    setTimeout(()=>{
      scrolling=false;
    },200)
  }
  determineSection = (isDown,currentSection) => {
    this.setState({scrolLLocation: document.getElementById(currentSection).getBoundingClientRect().bottom})
    if(isDown){
      if(currentSection==='home'){
        this.windowScroll('contact')
      }
      else if(currentSection==='contact'){
        this.windowScroll('form')
      }
    }
    else if (!isDown){
      if(currentSection==='form'){
        this.windowScroll('contact')
      }
      else if(currentSection==='contact'){
        this.windowScroll('home')
      }
    }
    else
      console.log('direction error')
  }
  handleNav = (e,anchor) => {

    this.setState((prevState)=>({section:anchor}));
  }
  windowScroll = (section) =>{
    let el = document.getElementById(section)
    console.log(`now: ${el.getBoundingClientRect().top}`)
    el.scrollIntoView()
  }
  render() {
    return (
      <div className="" style={{height:"100vh"}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            key={key}
            theKey={key}
            backgroundStyle={sectionList[key].backgroundStyle} 
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
