import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}

let keys = {37: 1, 38: 1, 39: 1, 40: 1};

function preventDefault(e) {
  e = e || window.event;
  if (e.preventDefault)
      e.preventDefault();
  e.returnValue = false;  
}

function preventDefaultForScrollKeys(e) {
    if (keys[e.keyCode]) {
        preventDefault(e);
        return false;
    }
}

function disableScroll() {
  if (window.addEventListener) // older FF
      window.addEventListener('DOMMouseScroll', preventDefault, false);
  document.addEventListener('wheel', preventDefault, {passive: false}); // Disable scrolling in Chrome
  window.onwheel = preventDefault; // modern standard
  window.onmousewheel = document.onmousewheel = preventDefault; // older browsers, IE
  window.ontouchmove  = preventDefault; // mobile
  document.onkeydown  = preventDefaultForScrollKeys;
}

function enableScroll() {
    if (window.removeEventListener)
        window.removeEventListener('DOMMouseScroll', preventDefault, false);
    document.removeEventListener('wheel', preventDefault, {passive: false}); // Enable scrolling in Chrome
    window.onmousewheel = document.onmousewheel = null; 
    window.onwheel = null; 
    window.ontouchmove = null;  
    document.onkeydown = null;  
}

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 1,
  }
  componentDidMount () {
    window.scrollTo(0,1)
    window.addEventListener('scroll', this.handleScroll)
  }
  componentDidUpdate () {
    window.scrollTo(0,1)
  }
  handleScroll = () => {
    let section = this.state.section;
    if (section===this.state.section){
      let st = window.pageYOffset || document.documentElement.scrollTop; // Credits: "https://github.com/qeremy/so/blob/master/so.dom.js#L426"
      console.log(st)
      if (st > this.state.scrollLocation){
        this.determineSection(true)
      } else {
        this.determineSection(false)
      }
    }
  }
  determineSection = (isDown) => {
    if(isDown){
      if(this.state.section==='home'){
        this.setState({section:'contact',scrollLocation:1})
      }
      else if(this.state.section==='contact'){
        this.setState({section:'form',scrollLocation:1})
      }
    }
    else{
      if(this.state.section==='form'){
        this.setState({section:'contact',scrollLocation:1})
      }
      else if(this.state.section==='contact'){
        this.setState({section:'home',scrollLocation:1})
      }
    }
    console.log('direction error')
  }
  handleNav = (e,anchor) => {
    this.setState((prevState)=>({section:anchor}));
  }
  render() {
    return (
      <div className="" style={{height:"100vh"}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        <Section
          backgroundStyle={sectionList[this.state.section].backgroundStyle} 
          body={sectionList[this.state.section].body}/>
      </div>
    );
  }
}

export default App;
