import React from 'react';
import PropTypes from 'prop-types';

class Slideshow extends React.Component{
  state = {
    slideIndex: 0,
    doc: {}
  }
  componentDidUpdate(){
    this.displayCurrentSlide();
  }
  nextSlide (e) {
    e.preventDefault();
    if(this.state.slideIndex===0)
      this.setState({slideIndex:this.props.formList.length-1});
    else
      this.setState((prevState)=>({slideIndex:prevState.slideIndex-1}));
    this.noDisplaySlide();
  }
  prevSlide (e) {
    e.preventDefault();
    if(this.state.slideIndex===this.props.formList.length-1)
      this.setState({slideIndex:0});
    else
      this.setState((prevState)=>({slideIndex:prevState.slideIndex+1}));
  }
  chooseSlide(e,index){
    e.preventDefault();
    this.setState({slideIndex:index})
  }
  noDisplaySlide () {
    const carouselList = document.getElementsByClassName('item');
    for(let carousel of carouselList){
      carousel.style.display = "none";
      carousel.classList.remove('active');
    }
    const dotList = document.getElementsByClassName('dot');
    for(let dot of dotList){
      dot.classList.remove('active');
    }
  }
  displayCurrentSlide() {
    const item = document.getElementById(`carousel${this.state.slideIndex}`);
    if(item!==null){
      item.style.display = "flex";
      item.classList.add('active');
    }
    const dot = document.getElementById(`indicator${this.state.slideIndex}`);
    if(dot!==null)
      dot.classList.add('active');
  }
  render(){
    return(
      <React.Fragment>
        <div id="slideshow" className="m-2 align-self-center" style={{backgroundColor:'rgba(0, 0, 0, 0.2)', height:'50vh'}}>
          <ol className="carousel-indicators">
            {this.props.formList.map((form,index)=>(
              <li key={index} id={`indicator${index}`} onClick={(e)=>this.chooseSlide(e,index)} className={`dot ${index===0?('active'):('')}`}></li>
            ))}
          </ol>
          <div className="w-100 d-sm-none d-md-flex h-100">
            <button id="prevButton" className="col-md-1 p-0 slideButton" onClick={(e)=>this.prevSlide(e)} style={{outline:'none'}}>&#10094;</button>
            <div className="col-md-10 align-self-center m-auto">
            {this.props.formList.map((formChunk,index)=>(          
              <div key={index} id={`carousel${index}`} className={`item col-md-12 ${index===0?('active d-flex'):('d-none')}`}>   
              {formChunk.map(form=>(
                <div key={form.url} className="col-md-3 p-2">
                  <a href={form.url} className="h-100 w-100">
                    <img className="w-100" src={form.sample} alt="Need Form"/>
                    <p className="text-center p-0 mx-2 my-1" style={{color:'white'}}><strong>{`${form.title}`}</strong></p>
                  </a>
                </div>
              ))}
              </div>
            ))}
            </div>
            <button id="nextButton" className="col-md-1 p-0 slideButton" onClick={(e)=>this.nextSlide(e)} style={{outline:'none'}}>&#10095;</button>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

Slideshow.propTypes={
  formList: PropTypes.object.isRequired
}

export default Slideshow;
