import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}
const directSectionList = {
  'home':{
    'up':'home',
    'down':'contact'
  },
  'contact':{
    'up':'home',
    'down':'form'
  },
  'form':{
    'up':'contact',
    'down':'form'
  }
}

let scrolling = false;
const checkScrolling = (isEqual) => {
  setInterval(()=>{
    if(scrolling = false){
      clearInterval(this)
    }
    else{
      if(isEqual=='equal'){
        scrolling = false;
        clearInterval(this);
      }
    }
  },5)
}

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
  }
  componentDidMount () {
    window.addEventListener('scroll', (e)=>this.scrollToNextSection(e))
    window.location.replace('/#home')
  }
  componentDidUpdate () {
    checkScrolling(this.relateViewToSection())
    if(this.state.section===undefined || this.state.section===null){}
    else{
      const nextSectionDoc = this.getDocument(this.state.section)
      nextSectionDoc.scrollIntoView({ behavior: 'smooth'})
    }
  }
  scrollToNextSection = (e) => {
    e.stopPropagation();
    e.stopImmediatePropagation();
    if (!scrolling){
      scrolling = true;
      const nextSection = this.determineNextSection();
      this.setState(()=>({section:nextSection}))
    } 
  }
  getDocument (section) {
    return document.getElementById(section)
  }
  determineNextSection = () => {
    return directSectionList[this.state.section][this.relateViewToSection()]
  }
  handleNav = (e,anchor) => {
    checkScrolling()
    this.setState((prevState)=>({section:anchor}));
  }
  relateViewToSection = () =>{
    const topBound = this.getDocument(this.state.section).getBoundingClientRect().top;
    if (topBound>0)
      return 'up';
    else if (topBound<0)
      return 'down';
    else{
      return 'equal';
    }
  }
  render() {
    return (
      <div className="" style={{height:"100vh"}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            key={key}
            theKey={key}
            backgroundStyle={sectionList[key].backgroundStyle} 
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
