import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      top: '0'
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '0'
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '100vh'
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      top: '200vh',
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '200vh'
    },
    body: Form
  }
}
const directSectionList = {
  'home':{
    'up':'home',
    'down':'contact'
  },
  'contact':{
    'up':'home',
    'down':'form'
  },
  'form':{
    'up':'contact',
    'down':'form'
  }
}

let isScrolling = false;

let distance = 0;
let test1 = 90;

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
    direction: 'down'
  }
  componentDidMount () {
    window.addEventListener('scroll',()=>this.test());
  }
  componentDidUpdate () {
    this.scrollToNextSection()
  }
  test(){
    let velocity = 0;
    setInterval(()=>{
      velocity -= 1;
      let doc = this.getDocument(this.state.section);
      doc.style.top = velocity +'%';
      console.log(doc.style.top)
    },1000)
  }
  scrollToNextSection(){
    if(!isScrolling){
      isScrolling = true;
      const nextSectionDoc = this.getDocument(this.state.section)
      let startScroll = false;
      console.log('going')
      const scroll = setInterval(()=>{
        if(!startScroll){
          console.log('start window')
          startScroll = true;
        }
        console.log(window.pageYOffset)
        if ((window.pageYOffset<nextSectionDoc.offsetTop+10&&window.pageYOffset<nextSectionDoc.offsetTop-10)){
          isScrolling = false;
          clearInterval(scroll)
          this.startScroll()
          console.log('stop')
          distance = 0;
        }
      },100)
      console.log('end')
    }
  }
  updateSection () {
    const nextSection = this.determineNextSection();
    this.setState(()=>({section:nextSection}));
  }
  getDocument (section) {
    return document.getElementById(section)
  }
  determineNextSection = () => {
    return directSectionList[this.state.section][this.relateViewToSection()]
  }
  handleNav = (e,anchor) => {
    this.setState((prevState)=>({section:anchor}));
  }
  relateViewToSection = () =>{
    const topBound = this.getDocument(this.state.section).getBoundingClientRect().top;
    if (topBound>0)
      return 'up';
    else if (topBound<0)
      return 'down';
    else
      return 'equal';
  }
  render() {
    return (
      <div className="h-100 w-100" style={{overflow:'initial'}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            key={key}
            theKey={key}
            backgroundStyle={sectionList[key].backgroundStyle} 
            sectionStyle={sectionList[key].sectionStyle}
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
