import React from 'react';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      top: '0'
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '0'
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '100vh'
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '100vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
      top: '200vh',
    },
    sectionStyle: {
      height:'100vh',position:'absolute', top: '200vh'
    },
    body: Form
  }
}
const directSectionList = {
  'home':{
    'up':'home',
    'down':'contact'
  },
  'contact':{
    'up':'home',
    'down':'form'
  },
  'form':{
    'up':'contact',
    'down':'form'
  }
}

let isScrolling = false;

let distance = 0;
let test1 = 90;
let scrollDistance = 0;
let prevScroll = 0;

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
    direction: 'down'
  }
  componentDidMount () {
    window.addEventListener('scroll',()=>this.move());
  }
  componentDidUpdate () {
  }
  move(){
    const direction = this.relateViewToSection();
    this.moveSection('contact',direction);
    this.moveSection('form',direction);
    console.log(this.relateViewToSection())
  }
  moveSection(section,direction){
    let doc = this.getDocument(section);
    let docPosition = doc.style.top.replace('vh','');
    let homeHeight = this.getDocument('home').offsetHeight;
    const start = setInterval(()=>{
      if(direction==='up')
        docPosition += 1;
      else
        docPosition -= 1;
      doc.style.top = docPosition +'%';
      console.log(homeHeight-doc.style.top)
      if(0===parseInt(homeHeight-doc.style.top))
        clearInterval(start)
    },5)
  }
  getDocument (section) {
    return document.getElementById(section)
  }
  determineNextSection = () => {
    return directSectionList[this.state.section][this.relateViewToSection()]
  }
  handleNav = (e,anchor) => {
    this.setState((prevState)=>({section:anchor}));
  }
  relateViewToSection = () =>{
    const topBound = this.getDocument(this.state.section).getBoundingClientRect().top;
    console.log(`${this.state.section}, ${topBound} `)
    if (topBound>=prevScroll){
      prevScroll=topBound;
      return 'up';
    }
    else if (topBound<0){
      prevScroll=topBound;
        return 'down';
    }
  }
  render() {
    return (
      <div className="h-100 w-100" style={{overflow:'initial'}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            key={key}
            theKey={key}
            backgroundStyle={sectionList[key].backgroundStyle} 
            sectionStyle={sectionList[key].sectionStyle}
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
