import React from 'react';
import { fadeIn } from 'react-animations';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 0,
  }
  componentDidMount () {
    window.location.replace('/#home')
    window.addEventListener('scroll', (e)=>this.handleScroll(e))
  }
  componentDidUpdate () {
  }
  handleScroll = (e) => { 
    let section = this.state.section;
    if (section===this.state.section){
      let st = window.pageYOffset || document.documentElement.scrollTop;
      if (st > this.state.scrollLocation){
        this.determineSection(true,this.state.section)
      } else {
        this.determineSection(false,this.state.section)
      }
      this.setState({scrollLocation:st===0?0:st})
    }
  }
  determineSection = (isDown,currentSection) => {
    console.log(`${isDown} + ${currentSection}`)
    if(isDown){
      if(currentSection==='home'){
        let el = document.getElementById(currentSection)
        el.scrollIntoView()
        this.setState({section:'contact'})
      }
      else if(currentSection==='contact'){
        let el = document.getElementById(currentSection)
        el.scrollIntoView()
        this.setState({section:'contact'})
      }
    }
    else{
      if(currentSection==='form'){
        let el = document.getElementById(currentSection)
        el.scrollIntoView()
        this.setState({section:'contact'})
      }
      else if(currentSection==='contact'){
        let el = document.getElementById(currentSection)
        el.scrollIntoView()
        this.setState({section:'contact'})
      }
    }
    console.log('direction error')
  }
  handleNav = (e,anchor) => {
    this.setState((prevState)=>({section:anchor}));
  }
  render() {
    return (
      <div className="" style={{height:"100vh"}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        {Object.keys(sectionList).map(key=>(
          <Section
            backgroundStyle={sectionList[key].backgroundStyle} 
            body={sectionList[key].body}/>
        ))}
      </div>
    );
  }
}

export default App;
