import React from 'react';
import { fadeIn } from 'react-animations';
import './App.css';

import Nav from './components/Nav';
import Section from './components/Section';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import Form from './components/Form';


import FrontBackground from './image/front.jpg';
import ContactBackground from './image/contact.jpg';
import FormBackground from './image/wood.jpg';

const sectionList = {
  home: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FrontBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: Home
  },
  contact: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${ContactBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover',
    },
    body: ContactUs
  },
  form: {
    navStyle: {
      color: 'white'
    },
    backgroundStyle: {
      backgroundImage: `url(${FormBackground})`,
      height: '101vh',
      backgroundPosition:'center',
      backgroundRepeat: 'no-repeat',
      backgroundSize: 'cover'
    },
    body: Form
  }
}

class App extends React.Component {
  state = {
    section: 'home',
    scrollLocation: 5,
  }
  componentDidMount () {
    window.location.replace('/#home')
    window.scrollTo(0,window.innerHeight / 10)
    window.scrollBy(0,window.innerHeight / 200)
    window.addEventListener('scroll', this.handleScroll)
  }
  componentDidUpdate () {
    window.scrollTo(0,window.innerHeight / 101)
    window.scrollBy(0,window.innerHeight / 200)
  }
  handleScroll = () => {  
    let section = this.state.section;
    if (section===this.state.section){
      let st = window.pageYOffset || document.documentElement.scrollTop; // Credits: "https://github.com/qeremy/so/blob/master/so.dom.js#L426"
      console.log(this.state.scrollLocation)
      console.log(window.pageYOffset)
      console.log(document.documentElement.scrollTop)
      console.log(`new location = ${st}`)
      if (st > this.state.scrollLocation){
        this.determineSection(true)
      } else {
        this.determineSection(false)
      }
      this.setState({scrollLocation:st})
    }
  }
  determineSection = (isDown) => {
    if(isDown){
      if(this.state.section==='home'){
        window.location.replace("/#contact")
        this.setState({section:'contact'})
      }
      else if(this.state.section==='contact'){
        window.location.replace("/#form")
        this.setState({section:'form'})
      }
    }
    else{
      if(this.state.section==='form'){
        window.location.replace("/#contact")
        this.setState({section:'contact'})
      }
      else if(this.state.section==='contact'){
        window.location.replace("/#home")
        this.setState({section:'home'})
      }
    }
    console.log('direction error')
  }
  handleNav = (e,anchor) => {
    this.setState((prevState)=>({section:anchor}));
  }
  render() {
    return (
      <div className="" style={{height:"100vh"}}>
        <Nav navStyle={sectionList[this.state.section].navStyle} handleNav={this.handleNav}/>
        <Section
          backgroundStyle={sectionList[this.state.section].backgroundStyle} 
          body={sectionList[this.state.section].body}/>
      </div>
    );
  }
}

export default App;
